import { IconButton, TextField } from "@material-ui/core";
import Radio from "@material-ui/core/Radio";
import { makeStyles } from "@material-ui/core/styles";
import { SearchOutlined } from "@material-ui/icons";
import axios from "axios";
import { nanoid } from "nanoid";
import { default as React,  useState } from "react";
import { Link } from "react-router-dom";
import Helmet from "../components/Helmet";
import dataPV from "../components/Private_data.json";

const useStyles = makeStyles(() => ({
  inputInput: {
    minWidth: "400px",
    marginBottom: "-20px",

    border: 0,
    borderRadius: 20,
    color: "white",
  },
  iconee: {
    fontSize: "30px",
  },
  radio1: {
    height: 40,
    marginRight: 100,
    marginLeft: "10px",
  },
  radio2: {
    height: 40,
  },
  radio1P: {
    height: 40,
    marginRight: 100,
  },
  radio2P: {
    height: 40,
    marginLeft:"-5px"
  },
}));

const Private = (props) => {
  const classes = useStyles();
  // using useState to set state foe values
  const [name, Setname] = useState("");
  const [type, Settype] = useState("file");
  const [inputP, setInputP] = useState("false");

  const [newurl, setNewurl] = useState("...");


  const [errorMessages, setErrorMessages] = useState({});

  const errors = {
    uname: "Please enter file name",
    pass: "please choose File or Folder",
    PorP: "please choose Public or Private",
    success: "successfull copied",
  };

  const addFrom = async () => {
    document.getElementById("reset").value = "";
    document.getElementById('Choose_Yes').checked = false;



    if (name === "") {
      setErrorMessages({ name: "uname", message: errors.uname });
    } else if (type === undefined || type === "") {
      setErrorMessages({ name: "pass", message: errors.pass });
    } else if (inputP === undefined) {
      setErrorMessages({ name: "PorP", message: errors.PorP });
    } else {
      var newform = {
        filename: name,
        isPrivate: inputP,
        type: type,
      };


    }
    let token = localStorage.getItem('accessToken')
      useEffect (() =>{
        const fetchId = async() =>{
          const Id = await axios({
           method: "POST",
           url: "http://localhost:5000/api/create/",
           data: newform,
           headers: { authorization: 'token: ' + token },
          })
         Setname("");
         Settype("");

         setNewurl(Id.data.data.fileID);
         console.log("form", newform)

       }
     }, [token])
  }

  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className="error">{errorMessages.message}</div>
    );


  const [text, setText] = useState("");

  const inputHandler = () => {
    setText("http://localhost:5000/" + newurl);
  };

  const copy = async () => {
    await navigator.clipboard.writeText(text);

    setErrorMessages({ name: "success", message: errors.success });
  };

  const [contacts, setContacts] = useState(dataPV);
  const [addFormData, setAddFormData] = useState({
    Name: "",
    Size: "",
    Dates: "",
    Download: "",
  });

  const handleAddFormChange = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;

    const newFormData = { ...addFormData };
    newFormData[fieldName] = fieldValue;

    setAddFormData(newFormData);
  };

  const handleAddFormSubmit = (event) => {
    event.preventDefault();

    const newContact = {
      id: nanoid(),
      Name: addFormData.Name,
      Size: addFormData.Size,
      Dates: addFormData.Dates,
      Download: addFormData.Download,
    };

    const newContacts = [...contacts, newContact];
    setContacts(newContacts);
  };

  const handleDeleteClick = (contactId) => {
    const newContacts = [...contacts];

    const index = contacts.findIndex((contact) => contact.id === contactId);

    newContacts.splice(index, 1);

    setContacts(newContacts);
  };

  const Logout_page = () => {
    props.history.push("/");
  };

  const handleChange = (event) => {
    Settype(event.target.value);
  };

  const handleP = (e) => {
    setInputP(e.target.value);
  };

  console.log("inputfile",type)

  return (
    <Helmet title="Public page">
      <div style={{ textAlign: "center", marginBottom: "-20px" }}>
        <img
          src="../images/logo_TTU.png"
          style={{ height: "150px", width: "130px" }}
        />
      </div>

      <div className="formaddtext">
        <div>
          <TextField
            className={classes.inputInput}
            fullWidth
            id="reset"
            type="text"
            onChange={(e) => Setname(e.target.value)}
            name="uname"
            variant="outlined"
            placeholder="Create File/Folder"
            InputProps={{
              style: {
                borderRadius: 100,
                paddingLeft: "50px",

                fontSize: 15,
              },

              endAdornment: (
                <IconButton>
                  <SearchOutlined
                    className={classes.iconee}
                    onClick={() => addFrom()}
                  />
                </IconButton>
              ),
            }}
          />
          {renderErrorMessage("uname")}
        </div>
      </div>

      <section>
        <div className="Logout" style={{ textAlign: "right" }}>
          <button
            className="button_new"
            style={{ marginTop: "50px", marginBottom: "50px" }}
            onClick={() => Logout_page()}
          >
            <i class="bx bx-log-out"></i>
          </button>
        </div>
      </section>
      <div></div>

      <div style={{ textAlign: "center" }}>
        File
        <Radio
          checked={type === "file"}
          onChange={handleChange}
          value="file"
          name="pass"
          className={classes.radio1}
        />
        Folder
        <Radio
          checked={type === "folder"}
          onChange={handleChange}
          value="folder"
          name="pass"
          className={classes.radio2}
        />
        <div> {renderErrorMessage("pass")}</div>
      </div>



      <div style={{ textAlign: "center" }}>
        Public
        <Radio
          checked={inputP === "false"}
          onChange={handleP}
          value="false"
          name="PorP"
          id ="Choose_Yes"
          className={classes.radio1P}
        />
        Private
        <Radio
          checked={inputP === "true"}
          onChange={handleP}
          value="true"
          name="PorP"
          id = "Choose_Yes"
          className={classes.radio2P}
        />
        <div> {renderErrorMessage("PorP")}</div>
      </div>

      <div className="url" style={{ textAlign: "center" }}>
        <h3>
          This is your URL:{" "}
          <span style={{ color: "red", cursor: "pointer" }}>
            {" "}
            <Link to={newurl} target="_blank">
              {" "}
              {"http://localhost:5000/" + newurl}
            </Link>{" "}
          </span>{" "}
        </h3>
      </div>

      <div style={{ textAlign: "center" }}>
        <input
          className="inputgetURL"
          type="text"
          name="success"
          value={text}
          onChange={inputHandler}
          style={{ width: "400px", height: "30px" }}
          placeholder="Enter spacebar to get link "
        />

        <div style={{ marginLeft: "350px", marginTop: "-33px" }}>
          <button
            onClick={copy}
            disabled={!text}
            style={{
              background: "none",
              border: "none",
              marginLeft: "10px",
              paddingTop: "-10px",
            }}
          >
            <i
              class="bx bx-copy"
              variant="inherit"
              style={{ fontSize: "30px", lineHeight: "20px" }}
            ></i>
          </button>
        </div>
      </div>

      <div style={{ textAlign: "center" }}>{renderErrorMessage("success")}</div>

      {/* <div className="app-container">
        <form className="formtabledata">
          <table className="tablecontent">
            <thead>
              <tr>
                <th>Name</th>
                <th>Size</th>
                <th>Date</th>
                <th>Download</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {contacts.map((contact) => (
                <Fragment>
                  <ReadOnlyRow contact={contact} handleDeleteClick={handleDeleteClick} />
                </Fragment>
              ))}
            </tbody>
          </table>
        </form>
      </div> */}
    </Helmet>
  );
};

export default Private;
